import React, { useState, useEffect } from "react";
import { withRouter } from "react-router";
import styled from "styled-components";
import ContentText from "../ContentText/ContentText";
import { useLocation } from "react-router-dom";

import {
  CRM1,
  CRM2,
  CRM3,
  CRM4,
  CRM5,
  CRM6,
  CRM7,
  CRMThumbnail,
  MCRM1,
  MCRM2,
  MCRM3,
  MCRM4,
  MCRM5,
  MCRM6,
  MCRM7,
  MCRMThumbnail,
  partner1,
  partner2,
  partner3,
  partner4,
  partner5,
  partner6,
  partnerThumbnail,
  Mpartner1,
  Mpartner2,
  Mpartner3,
  Mpartner4,
  Mpartner5,
  Mpartner6,
  MpartnerThumbnail,
  address1,
  address2,
  address3,
  address4,
  address5,
  address6,
  addressThumbnail,
  Maddress1,
  Maddress2,
  Maddress3,
  Maddress4,
  Maddress5,
  Maddress6,
  MaddressThumbnail,
  vote1,
  vote2,
  vote3,
  vote4,
  vote5,
  vote6,
  vote7,
  voteThumbnail,
  Mvote1,
  Mvote2,
  Mvote3,
  Mvote4,
  Mvote5,
  Mvote6,
  MvoteThumbnail,
  BEAPON1,
  BEAPON2,
  BEAPON3,
  BEAPONThumbnail,
  MBEAPON1,
  MBEAPON2,
  MBEAPON3,
  MBEAPONThumbnail,
} from "../portfolioImg";

const PofolSelect = ({ ...props }) => {
  useEffect(() => {}, []);

  const [state, setState] = useState("ALL");
  const [windowSize, setWindowSize] = useState(window.innerWidth);

  const selectTab = [
    { title: "ALL", check: "ALL" },
    { title: "WEB", check: "WEB" },
    { title: "APP", check: "APP" },
    { title: "GAME", check: "GAME" },
  ];

  const PortfolioList = [
    {
      id: 1,
      name: "CRM",
      date: "2020.02.02",
      additionEx:
        "무료로 사용하는 농수산물 직거래장터! 무료로 사용하는 농수산물 직거래 장터!",
      nh: "NH농협 | App Android, IOS",
      thumbnail: CRMThumbnail,
      Mthumbnail: MCRMThumbnail,
      baneerImg: CRM1,
      mBannerImg: MCRM1,
      img: [CRM2, CRM3, CRM4, CRM5, CRM6, CRM7],
      mImg: [MCRM2, MCRM3, MCRM4, MCRM5, MCRM6, MCRM7],
      check: "WEB",
    },
    {
      id: 2,
      name: "건축파트너",
      date: "2020.02.02",
      additionEx:
        "무료로 사용하는 농수산물 직거래장터! 무료로 사용하는 농수산물 직거래 장터!",
      nh: "NH농협 | App Android, IOS",
      thumbnail: partnerThumbnail,
      Mthumbnail: MpartnerThumbnail,
      baneerImg: partner1,
      mBannerImg: Mpartner1,
      img: [partner2, partner3, partner4, partner5, partner6],
      mImg: [Mpartner2, Mpartner3, Mpartner4, Mpartner5, Mpartner6],
      check: "WEB",
    },
    {
      id: 3,
      name: "투표창고",
      date: "2020.02.02",
      additionEx:
        "무료로 사용하는 농수산물 직거래장터! 무료로 사용하는 농수산물 직거래 장터!",
      nh: "NH농협 | App Android, IOS",
      thumbnail: voteThumbnail,
      Mthumbnail: MvoteThumbnail,
      baneerImg: vote1,
      mBannerImg: Mvote1,
      img: [vote2, vote3, vote4, vote5, vote6, vote7],
      mImg: [Mvote2, Mvote3, Mvote4, Mvote5, Mvote6],
      check: "WEB",
    },
    {
      id: 4,
      name: "주소록",
      date: "2020.02.02",
      additionEx:
        "무료로 사용하는 농수산물 직거래장터! 무료로 사용하는 농수산물 직거래 장터!",
      nh: "NH농협 | App Android, IOS",
      thumbnail: addressThumbnail,
      Mthumbnail: MaddressThumbnail,
      baneerImg: address1,
      mBannerImg: Maddress1,
      img: [address2, address3, address4, address5, address6],
      mImg: [Maddress2, Maddress3, Maddress4, Maddress5, Maddress6],
      check: "WEB",
    },
    {
      id: 4,
      name: "BEAPON",
      date: "2020.02.13",
      additionEx:
        "무료로 사용하는 농수산물 직거래장터! 무료로 사용하는 농수산물 직거래 장터!",
      nh: "NH농협 | App Android, IOS",
      thumbnail: addressThumbnail,
      Mthumbnail: MaddressThumbnail,
      baneerImg: address1,
      mBannerImg: Maddress1,
      img: [BEAPON1,
        BEAPON2,
        BEAPON3,],
      mImg: [Maddress2, Maddress3, Maddress4, Maddress5, Maddress6],
      check: "WEB",
    },
  ];

  const a = ({ id, data }) => {
    props.history.push(`/portfolio/${id}`, data);
  };

  function handleChange(e) {
    setState(e.target.value);
  }

  useEffect(() => {
    const handleResize = () => setWindowSize(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <PortfolioWrap>
      <div className="optionStyle">
        <select name="option" onChange={handleChange} value={state}>
          {selectTab.map((data, idx) => (
            <option key={idx} value={data.check}>
              {data.title}
            </option>
          ))}
        </select>
      </div>
      <div className="selectStyle">
        <div>
          {selectTab.map((data, idx) => (
            <div key={idx}>
              <p
                onClick={() => {
                  setState(data.check);
                }}
                style={{
                  fontWeight: state === data.check ? "bold" : "400",
                }}
              >
                {data.title}
              </p>
            </div>
          ))}
        </div>
      </div>
      <div className="portfolioStyle">
        {PortfolioList.filter(
          (data) => data.check === state || state === "ALL"
        ).map((data) => (
          <div
            className="PortfolioBox"
            key={data.id}
            onClick={() => a({ id: data.id, data }, window.scrollTo(0, 0))}
          >
            <div className="imgBox">
              <img
                src={windowSize >= 480 ? data.thumbnail : data.Mthumbnail}
                alt={data.thumbnail}
              />
            </div>
            <div className="textBox">
              <div>
                <p className="PofolName">{data.name}</p>
                <p className="PofolDate">{data.date}</p>
              </div>
              <p className="addEx">{data.additionEx}</p>
              <p className="nhText">{data.nh}</p>
            </div>
          </div>
        ))}
      </div>
    </PortfolioWrap>
  );
};

const PortfolioWrap = styled.div`
  width: 100%;
  height: 100%;

  .optionStyle {
    display: none;
  }

  .selectStyle {
    width: 100%;
    height: 40px;

    display: flex;
    align-items: center;
    cursor: pointer;

    div {
      width: 280px;

      display: flex;
      justify-content: space-between;
      align-items: center;
    }
  }

  .portfolioStyle {
    width: 100%;
    height: calc(100% - 200px);

    margin-top: 50px;

    .PortfolioBox:nth-child(4n + 4) {
      margin-right: 0;
    }
    .PortfolioBox {
      width: calc(100% / 4 - 23px);

      float: left;

      margin: 0 30px 40px 0;

      cursor: pointer;
      .imgBox {
        width: 100%;

        display: flex;
        justify-content: center;
        align-items: center;
        img {
          max-width: 100%;
          max-height: 100%;

          object-fit: cover;
        }
      }

      .textBox {
        width: 100%;
        height: 150px;

        position: relative;
        div {
          width: 100%;

          display: flex;
          justify-content: space-between;
          align-items: center;

          .PofolName {
            color: #333333;
            font-size: 1.1em;
            font-weight: bold;

            float: left;

            margin-top: 20px;
          }
          .PofolDate {
            color: #999999;
            font-size: 0.9em;

            float: left;
            margin-top: 20px;
          }
        }
        .addEx {
          color: #333333;
          font-size: 0.9em;
          font-weight: normal;

          margin-top: 10px;
        }

        .nhText {
          width: 160px;

          color: #999999;
          font-size: 0.8em;

          float: left;

          position: absolute;
          left: 100%;
          top: 85%;
          transform: translate(-100%, -85%);
        }
      }
    }
  }

  @media only screen and (max-width: 1024px) {
    .selectStyle {
      margin-top: 20px;
    }
    .portfolioStyle {
      .PortfolioBox:nth-child(2n + 2) {
        margin-right: 0;
      }
      .PortfolioBox {
        width: calc(100% / 2 - 10px);
        margin-right: 20px;
      }
    }
  }
  @media only screen and (max-width: 768px) {
    .selectStyle {
      margin-top: 12px;
    }
    .portfolioStyle {
      margin-top: 24px;
      .PortfolioBox {
        width: calc(100% / 2 - 8px);

