import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";
import Wrap from "../../components/Wrap/Wrap";
import Banner from "../../components/Banner/Banner";
import Content from "../../components/Content/Content";
import Footer from "../../components/Footer/Footer";
import styled from "styled-components";
import Media from "../../components/Media/Media";
import MediaLink from "../../components/MediaLink/MediaLink";


const HiringSub = ({ ...props }) => {
  let recruitState = props.location.state;
  useEffect(() => {
    if (recruitState === undefined) {
      alert("없는 값");
      props.history.push("/hiring");
    } else {
      console.log(recruitState);
    }
  }, []);

  return (
    <Wrap>
      <Banner>
        <div className="backgroundStyleHiring">
          <p className="hiringMainText">Hiring</p>
        </div>
      </Banner>
      <Content>
        <HiringStyle>
          <div className="recruitStyle">{recruitState.recruitText}</div>
          <div className="recruitStyle">{recruitState.hiringImg}</div>
        </HiringStyle>
      </Content>
      <Media>
        <Footer />
      </Media>
    </Wrap>
  );
};

const HiringStyle = styled.div`
  width: 60%;
  height: 100%;

  margin-bottom: 200px;

  .recruitStyle {
    font-size: 2.425em;
    color: #333333;
    font-weight: 600;

    margin-bottom: 20px;
  }

  .teamStyle {
    float: left;
    margin-right: 20px;

    font-size: 1.1em;
  }

  .dateStyle {
    float: left;

    font-size: 1.1em;
    color: #999999;
  }

  @media only screen and (max-width: 460px) {
    width: 100%;

    .recruitStyle {
      font-size: 1.1em;
      color: #333333;
      font-weight: 600;

      margin-bottom: 8px;
    }

    .teamStyle {
      float: left;
      margin-right: 8px;

      font-size: 0.9em;
    }

    .dateStyle {
      float: left;

      font-size: 0.9em;
      color: #999999;
    }
  }
`;

export default withRouter(HiringSub);
